SELECT revenueTotal.category_ID, revenueTotal.Category_Name, SUM(revenueTotal.Total_Revenue) AS Total_Revenue FROM (SELECT categories.category_ID, categories.name AS Category_Name, order_items.product_ID, (order_items.quantity * order_items.unit_price) AS Total_Revenue FROM order_items JOIN products ON order_items.product_ID = products.`product_ID` LEFT JOIN categories ON products.category_ID = categories.category_ID GROUP BY order_items.product_ID) AS revenueTotal GROUP BY revenueTotal.category_ID ORDER BY revenueTotal.Total_Revenue DESC